SELECT CO.CourseOfferId, CO.CourseId, CO.Year, CO.Quartile, C.CourseName, C.CourseDescription, C.DegreeId, C.ECTS, D.Dept, D.DegreeDescription, D.TotalECTS, T.TeacherId, T.TeacherName, T.Address, T.BirthyearTeacher, T.Gender FROM CourseOffers as CO, Courses as C, Degrees as D, Teachers as T, TeacherAssignmentsToCourses as TATC WHERE CO.CourseOfferId = 1 AND CO.CourseId = C.CourseId AND C.DegreeId = D.DegreeId AND CO.CourseOfferId = TATC.CourseOfferId AND TATC.TeacherId = T.TeacherId;
SELECT CO.CourseOfferId, CO.CourseId, CO.Year, CO.Quartile, S.StudentId, S.StudentName, S.Address, S.BirthyearStudent, S.Gender, D.DegreeId, D.Dept, D.DegreeDescription, D.TotalECTS FROM CourseOffers as CO, Courses as C, StudentRegistrationsToDegrees as SRTD, Students as S, Degrees as D WHERE SRTD.StudentRegistrationId = 3 AND CO.CourseId = C.CourseId AND C.DegreeId = SRTD.DegreeId AND SRTD.DegreeId = S.StudentId AND SRTD.DegreeId = D.DegreeId;
SELECT AVG(CR.Grade) FROM CourseRegistrations as CR WHERE CR.StudentRegistrationId = 3;
