SELECT CourseOffers.CourseOfferId, CourseOffers.CourseId, CourseOffers.Year, CourseOffers.Quartile, Courses.CourseName, Courses.CourseDescription, Courses.DegreeId, Courses.ECTS, Degrees.Dept, Degrees.DegreeDescription, Degrees.TotalECTS, Teachers.TeacherId, Teachers.TeacherName, Teachers.Address, Teachers.BirthyearTeacher, Teachers.Gender FROM CourseOffers, Courses, Degrees, Teachers, TeacherAssignmentsToCourses WHERE CourseOffers.CourseOfferId = 1 AND CourseOffers.CourseId = Courses.CourseId AND Courses.DegreeId = Degrees.DegreeId AND CourseOffers.CourseOfferId = TeacherAssignmentsToCourses.CourseOfferId AND TeacherAssignmentsToCourses.TeacherId = Teachers.TeacherId;
SELECT CourseOffers.CourseOfferId, CourseOffers.CourseId, CourseOffers.Year, CourseOffers.Quartile, Students.StudentId, Students.StudentName, Students.Address, Students.BirthyearStudent, Students.Gender, Degrees.DegreeId, Degrees.Dept, Degrees.DegreeDescription, Degrees.TotalECTS FROM CourseOffers, Courses, StudentRegistrationsToDegrees, Students, Degrees WHERE StudentRegistrationsToDegrees.StudentRegistrationId = 3 AND CourseOffers.CourseId = Courses.CourseId AND Courses.DegreeId = StudentRegistrationsToDegrees.DegreeId AND StudentRegistrationsToDegrees.DegreeId = Students.StudentId AND StudentRegistrationsToDegrees.DegreeId = Degrees.DegreeId;
SELECT AVG(CourseRegistrations.Grade) FROM CourseRegistrations WHERE CourseRegistrations.StudentRegistrationId = 3;
